import React, { Component } from 'react';

import './sass/main.scss';




import 'animate.css/animate.css';
// pages
import Mainpage from './pages/';
import NotFoundPage from './pages/404';
import ChowdownPage from './pages/chowdown';
import InkTankPage from './pages/inktank';
import SoulMarcosPage from './pages/soulmarcos';
import CareOf from './pages/careof';
//components
import Hero from './components/home/Hero';
class App extends Component {
  render() {
    return (
      <div className="App scroll-container">
<Mainpage></Mainpage>
<ChowdownPage></ChowdownPage>
  <NotFoundPage></NotFoundPage>
  <InkTankPage></InkTankPage>
  <CareOf></CareOf>





      </div>


    );
  }
}

const sections = [...document.querySelectorAll("section")];

let options = {
  rootMargin: "0px",
  threshold: 0.25
};

const callback = (entries, observer) => {
  entries.forEach(entry => {
    const { target } = entry;
    console.log(entry, target)

    if (entry.intersectionRatio >= 0.25) {
      target.classList.add("is-visible");
    } else {
      target.classList.remove("is-visible");
    }
  });
};

const observer = new IntersectionObserver(callback, options);

sections.forEach((section, index) => {
  observer.observe(section);
});

window.onload(removeClass());



export default App;
