import React, { Component } from 'react';
import Modal from 'react-awesome-modal';

export default class Popup extends Component {
    constructor(props) {
        super(props);
        this.state = {
            visible : false
        }
    }

    openModal() {
        this.setState({
            visible : true
        });
    }

    closeModal() {
        this.setState({
            visible : false
        });
    }

    render() {
        return (
          <React.Fragment>
            <section>
                <h3 className="secondary-header">graphic work </h3>
                <div className="graphic-work">
            <div className="graphic-design-1">
            </div>

        </div>
        <input type="button" value="illustation for Ink Tank " onClick={() => this.openModal()} />
                <Modal 
                    visible={this.state.visible}
                    width="500"
                    height="500"
                    effect="fadeInUp"
                    onClickAway={() => this.closeModal()}
                >
                    <div className="graphic-container">
                      <img src="https://i.ibb.co/zXVsmmL/design-for-website1.jpg" width="300px" height="auto"/>
                        <h1>Num Nom</h1>
                        <p>Some Contents</p>
                        <a href="javascript:void(0);" onClick={() => this.closeModal()}>Close</a>
                    </div>

                </Modal>
            </section>
            </React.Fragment>
        );
    }
}
