import React, { Component } from 'react';
import Modal from 'react-awesome-modal';

export default class Popup extends Component {
    constructor(props) {
        super(props);
        this.state = {
            visible : false
        }
    }

    openModal() {
        this.setState({
            visible : true
        });
    }

    closeModal() {
        this.setState({
            visible : false
        });
    }

    render() {
        return (
            <section>
                <h1>React-Modal Examples</h1>
                <input type="button" value="Open" />
                <div className="graphic-work">
            <div className="graphic-design-2">
            </div>
            <p> illustation for Ink Tank </p>
        </div>
                <Modal 
                    visible={this.state.visible}
                    width="400"
                    height="300"
                    effect="fadeInUp"
                    onClickAway={() => this.closeModal()}
                >
                    <div>
                      <img onClick={() => this.openModal()} src="https://i.ibb.co/zXVsmmL/design-for-website1.jpg" width="500px" height="auto"/>
                        <h1>Title</h1>
                        <p>Some Contents</p>
                        <a href="javascript:void(0);" onClick={() => this.closeModal()}>Close</a>
                    </div>

                </Modal>
            </section>


        );
    }
}
